class TokenContract:

    # Find the contract from chain
    def __init__(self):
        web3_provider ="http://120.27.237.152:8801"
        w3 = Web3(HTTPProvider(web3_provider))
        self.account = w3.eth.accounts[0]
        addr = Web3.toChecksumAddress(balanceAddr)
        self.contract_instance = w3.eth.contract(abi=json.loads(balanceABI), address=addr, ContractFactoryClass=ConciseContract)

    # -------------- interactive with contract -------------
    def balanceOf(self, account):
        result = self.contract_instance.balanceOf(account)
        return result


if __name__ == '__main__':

    profile_contacter = TokenContract()
    user_account = "0x488c94f355e8e671c493cdccc5e026d049fe3422"
    balance = profile_contacter.balanceOf(user_account)
    print("User balance: " )
